#!/usr/bin/env python
import collections
from isstring import isstring
from public import public


def isiterable(obj):
    return isinstance(obj, collections.Iterable)


@public
def tolist(obj):
    """convert object to list"""
    if obj is None:
        return []
    if isstring(obj):
        return [obj]
    if isiterable(obj):
        return list(obj)
    else:
        return [obj]
